function sw(obj,bf,cla,tn,tv){
	var	el,
		i,
		page;
		
	var slides = new Array();
	
	eval("var $o = document.getElementById('"+obj+"');");
	var $v = $o.nextSibling.nextSibling;
	$v = $v.nodeType == 3 ? $v.nextSibling : $v;

	for(var i=0;i<$v.childNodes.length;i++){
		if($v.childNodes[i].nodeType == 1){
			var myobj 	= $v.childNodes[i];
			var Ename	= myobj.nodeName;
			var Eclass	= myobj.getAttribute('class');
			var Ehtml	= myobj.innerHTML;
			
			var htm = "<"+Ename+" class='"+Eclass+"'>"+Ehtml+"</"+Ename+">";

			slides.push(htm);
		}
	}
		
	var setObj = obj + "= new SwipeView('#"+obj+"',{numberOfPages:slides.length});";
	eval(setObj);

	for (i=0; i<3; i++) {
		page = i==0 ? slides.length-1 : i-1;
		
		el = document.createElement('div');
		el.className = 'slbox';
		el.innerHTML = slides[page];
		eval(obj+".masterPages[i].appendChild(el)");
	}
	
	eval("var $bf = document.getElementById('"+bf+"');");
	var backopera  = "$bf.setAttribute('class','n'+"+obj+".pageIndex)";
	var cab = obj+".onFlip(function(){var el,upcoming,i;for(i=0;i<3;i++){upcoming = "+obj+".masterPages[i].dataset.upcomingPageIndex;if(upcoming != "+obj+".masterPages[i].dataset.pageIndex){el="+obj+".masterPages[i].querySelector('.slbox');;el.innerHTML = slides[upcoming];}}"+backopera+"});";
	eval(cab);
}

var TUIJIAN = null;sw('TUIJIAN','cb2');
var BBS = null;sw('BBS','cb3');
var ZIXUN = null;sw('ZIXUN','cb4');
//------

//输入框自动填充
$("#search .txt").val($('#search .txt').attr('title'));

$("#search .txt").focus(function(){
	document.body.scrollTop = 39;
	var v = $(this).attr('title');
	if($(this).val() == v){
		$(this).val('');
	}
}).blur(function(){
	var v = $(this).attr('title');
	if($(this).val() == ''){
		$(this).val(v);
	}
});
//------

//搜索框展示及Tab切换效果
$('#search .title span').click(function(){
	var value = $(this).attr('value');
	var keyword = $('#keyword').val();

	var title = ["请输入公司名，如：前程无忧", "请输入职位名，如：销售", "请输入关键字，如：销售"];
	var word;

	switch(value) {
		case '0' :
			word = title[0];
			break;
		case '1' :
			word = title[1];
			break;
		default :
			word = title[2];
			break;
	}

	if(-1 == $.inArray(keyword, title)) {
		$('#keyword').val(keyword);
	} else {
		$('#keyword').val(word);
	}
	$('#keyword').attr('title', word);
	$(this).addClass('on').siblings().removeClass('on');
});

$('#search input').focus(function(){
	$('#search .msg').show();
});

$('#search .up').click(function(){
	$('#search .msg').hide();
});
//------

//弹层效果
function pop(msg,mtype,but1,but2,fun1,fun2){
	//msg	: 提示框文本内容
	//mtype	: 文本内容前是否带感叹号？0:不带感叹号（默认值）；1:带感叹号
	//but1	: 按钮1文案，默认为"确认"
	//but2	: 按钮2文案，无按钮2则为空
	//fun1	: 按钮1回调函数，默认为关闭弹出层
	//fun2	: 按钮2回调函数
	var top = document.body.scrollTop || document.documentElement.scrollTop;
	var h = document.documentElement.clientHeight;
	var v = 0;
	
	if(h > 160){
		v = (h-160)/2;
	}
	
	top += v;
	
	mtype	= mtype ? mtype : 0; 
	but1 	= but1 ? but1 : '确认';
	fun1 	= fun1 ? fun1 : 'popclose()';
	fun2 	= fun2 ? fun2 : 'popclose()';

	if($('#pop')[0]){
		$('#pop').css('top',top);
		msg = mtype ? "<em></em>"+msg : msg;
		$('#pop p').html(msg);
		$('#pop .but1').val(but1).attr('onclick',fun1);
		if(but2){
			$('#pop .but2').val(but2).attr('onclick',fun2);
		}
		
		$('#pop,#shadow').show();
	}else{
		var htm  = "<div id='pop' class='bb' style='top:"+top+"px'>";
			htm += "<section>";
			msg = mtype ? "<em></em>"+msg : msg;
			htm += "<p>"+msg+"</p>";
			htm += "<div class='bop'>";
			htm += "<input class='but but1' onclick="+fun1+" type='button' value='"+but1+"' />";
			if(but2){
				htm += "<input class='but but2' onclick="+fun2+" type='button' value='"+but2+"' />";
			}
			htm += "</div>";
			htm += "</section>";
			htm += "</div>";
			htm += "<div id='shadow'></div>";
		
		$('body').append(htm);
		
		$('#pop,#shadow').show();
	}
}

function popclose(){
	$('#pop,#shadow').hide();
}
//搜索框展示及Tab切换效果

/*
 * 二级页面
 */
function pageshow(name){
	$('.page1').hide();
	$('#'+name).show();
	document.body.scrollTop = 0;
	document.documentElement.scrollTop = 0;
}

function pageclose(){
	$('.page1').show();
	$('.page2').hide();
	document.body.scrollTop = 0;
	document.documentElement.scrollTop = 0;
}


//取得联想的结果
function getSimpleResult(domain)
{
	var o = arguments[1] ? $(arguments[1]) : $('.on');
	var keyword = filterTitle($('#keyword'));
	var	keywordtype = o.attr('value');
	keyword = keyword.replace(/(^\s*)|(\s*$)/g,'');
	if(keyword == '')
	{
		$('#legend').hide();
		$('#searchlog').show();
		$('#searchlog').prev().show();
		return false;
	}
	var param = {'keyword':keyword,
				 'keywordtype':keywordtype};
	var url	= domain + 'ajax/in/simplesearch.ajax.php';
	var result = ajaxRequest(url,param,'post','json');
	if(result.status == 1)
	{
		$('#searchlog').hide();
		$('#searchlog').prev().hide();
		$('#legend').empty().append(result.str).show();
	}
}